import React, { useState, useContext } from "react";
import Form from "../Components/Form";
import { ContextGlobal } from "../Components/utils/global.context";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Contact = () => {

  const { theme } = useContext(ContextGlobal);

  const [datos, setDatos] = useState({});
  const [showOk, setShowOk] = useState(false);
  const [showError, setShowError] = useState(false);

  const enviarDatos = (datos) => {
    setDatos(datos);
    console.log(datos)
    setShowOk(true);
    setShowError(false);
  }

  return (
    <div style={{ color: theme.font, background: theme.background }}>
      <Form onEnviarDatos={enviarDatos} setShowOk={setShowOk} setShowError={setShowError} showError={showError}/>
      {showOk && <h5>Gracias {datos.nombre}, te contactaremos a la brevedad.</h5>}
    </div>
  );
};

export default Contact;
