import React, { useState, useEffect, useContext } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { ContextGlobal } from "../Components/utils/global.context";
import fotoDentista from "../images/doctor.jpg";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {

  const { theme } = useContext(ContextGlobal);

  const [dentista, setDentista] = useState([]);
  const params = useParams();
  const navigate = useNavigate();

  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico
  const getDentista = async () => {
    const res = await fetch(
      `https://jsonplaceholder.typicode.com/users/${params.id}`
    );
    const data = await res.json();
    setDentista(data);
  };

  useEffect(() => {
    getDentista();
  });

  {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
  return (
    <div style={{ color: theme.color, background: theme.background }}>
      <h1>+info Dentista #{dentista.id} </h1>
      <div className="container">
        <div className="card">
          <img src={fotoDentista} />
          <h4>{dentista.name}</h4>
          <p>{dentista.email}</p>
          <p>{dentista.phone}</p>
          <h5>{dentista.website}</h5>
          <button onClick={() => navigate(-1)} className="favButton" style={{ color: theme.color, background: theme.background }}>
            ⏪Atrás
          </button>
        </div>
      </div>
    </div>
  );
};

export default Detail;
