import React, {useContext} from "react";
import { ContextGlobal } from "../Components/utils/global.context";
import "../index.css";
import { Link } from "react-router-dom";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {

  const { theme, handleDarkTheme, handleLightTheme } = useContext(ContextGlobal);

  function handleTheme() {
    if(theme.font==="black") return handleDarkTheme;
    if(theme.font==="white") return handleLightTheme
  }

  return (
    <nav>
      {/* Aqui deberan agregar los liks correspondientes a las rutas definidas */}
      <li>
        <Link to="/">Home</Link>
      </li>
      <li>
        <Link to="/favs">Favoritos</Link>
      </li>
      <li>
        <Link to="/contacto">Contacto</Link>
      </li>

      {/* Deberan implementar ademas la logica para cambiar de Theme con el button */}
      <li>
        {/* <button className="favButton" onClick={handleDarkTheme}>Modo oscuro</button>
        <button className="favButton" onClick={handleLightTheme}>Modo claro</button> */}
        <button className="favButton" onClick={handleLightTheme}>Modo claro</button>
      </li>
    </nav>
  );
};

export default Navbar;
