import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "../Components/utils/global.context";
import fotoDentista from "../images/doctor.jpg";

const Card = ({ dentista }) => {
  const { theme, favs, setFavsInStorage} = useContext(ContextGlobal);

  const addFav = () => {
    // Aqui iria la logica para agregar la Card en el localStorage
    const getDentista = async () => {
      const res = await fetch(
        `https://jsonplaceholder.typicode.com/users/${dentista.id}`
      );
      const data = await res.json();
      setFavsInStorage(data);
    }



  };

  return (
    <Link key={dentista.id} to={`/dentist/${dentista.id}`}>
      {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

      <div
        className="card"
        style={{ color: theme.color, background: theme.background }}
      >
        <h4>{dentista.id}</h4>
        <img src={fotoDentista} alt="Foto dentista" />
        <h4>{dentista.name}</h4>
        <h3>{dentista.username}</h3>
        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
        <button
          onClick={addFav}
          className="favButton"
          style={{ color: theme.color }}
        >
          Agregar a favoritos
        </button>
      </div>
      //aca
    </Link>
  );
};

export default Card;
